// src/components/QuestionCard.js
import React from 'react';

export default function QuestionCard({ question, value, onChange, index, total }) {
  return (
  <div
    className="question-card d-flex flex-column justify-content-center align-items-center"
    style={{ minHeight: '80vh', padding: '1rem' }}
  >
    {/* Question counter */}
    <div className="question-number mb-2 text-center">
      Question {index + 1} of {total}
    </div>

    {/* The actual question text */}
    <div
      className="question-text mb-3 text-center"
      style={{ fontSize: '1.25rem', fontWeight: '500' }} // Increased size
    >
      {question.description}
    </div>

    {/* The three-point radio options */}
    <div className="options text-center">
      {['Rarely', 'Sometimes', 'Frequently'].map((label, i) => {
        const val = i + 1;
        return (
          <label key={val} className="d-block mb-2">
            <input
              type="radio"
              name={`q-${index}`}
              checked={value === val}
              onChange={() => onChange(val)}
              className="me-2"
            />
            {label}
          </label>
        );
      })}
    </div>
  </div>
);
}
