import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import ReactConfetti from 'react-confetti';
import { useWindowSize } from 'react-use';
import QuestionCard from '../components/QuestionCard';
import NavButtons from '../components/NavButtons';
import '../style.css';

export default function SurveyPage() {
  const API = process.env.REACT_APP_API_URL;
  const nav = useNavigate();
  const [surveys, setSurveys] = useState([]);
  const [selected, setSelected] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [resp, setResp] = useState({});
  const [idx, setIdx] = useState(0);
  const [desktop, setDesktop] = useState(window.innerWidth>768);
  const [showIntro, setShowIntro] = useState(true);
  const [submitted, setSubmitted] = useState(false);
  const {width,height} = useWindowSize();

  useEffect(()=>{
    axios.get(`${API}/usf/me`, {withCredentials:true})
      .then(r => { if(!r.data.logged_in) nav('/login'); else loadSurveys(); })
      .catch(()=>nav('/login'));
  },[]);

  const loadSurveys = ()=>{
    axios.get(`${API}/usf/surveys`,{withCredentials:true})
      .then(r=>setSurveys(r.data.surveys))
      .catch(console.error);
  };

  const loadQs = sid=>{
    axios.get(`${API}/usf/surveys/${sid}/questions`)
      .then(r=>{ setQuestions(r.data.questions); setIdx(0); setResp({}); setShowIntro(true); })
      .catch(console.error);
  };

  const totalScore = Object.entries(resp).reduce(
    (sum,[i,val]) => sum + (questions[i]?.weight||0)*val, 0
  );

  const handleSubmit = ()=>{
    setSubmitted(true);
  };

  if (!selected) {
    return (
      <div className="container my-5">
        <h2>Select a Survey</h2>
        <select className="form-select" onChange={e=>{setSelected(e.target.value); loadQs(e.target.value)}} value={selected||''}>
          <option value=''>-- choose one --</option>
          {surveys.map(s => <option key={s.id} value={s.id}>{s.title}</option>)}
        </select>
      </div>
    );
  }

  if (submitted) {
    return (
      <div className="thank-you text-center">
        <ReactConfetti width={width} height={height}/>
        <h2>Thank you!</h2>
        <p>You scored {totalScore}</p>
        <button className="btn-crimson" onClick={()=>nav('/')}>Return Home</button>
      </div>
    );
  }

  return (
    <div className="container my-4">
      {(desktop || showIntro) && (
        <>
          <h2>{surveys.find(s=>s.id==selected)?.title}</h2>
          <p>{surveys.find(s=>s.id==selected)?.description}</p>
          {!desktop && <button className="btn-crimson my-3" onClick={()=>setShowIntro(false)}>Start Survey</button>}
        </>
      )}

      {!desktop && !showIntro && questions.length>0 && (
        <>
          <QuestionCard question={questions[idx]} value={resp[idx]||0}
            onChange={v => setResp(prev=>({...prev,[idx]:v}))}
            index={idx} total={questions.length} show={true}
          />
          <NavButtons idx={idx} max={questions.length-1}
            onPrev={()=>setIdx(idx-1)} onNext={()=>setIdx(idx+1)}
            onSubmit={handleSubmit}
          />
        </>
      )}

      {desktop && questions.length>0 && (
        <table className="table table-striped">
          <thead><tr><th>#</th><th>Description</th><th>Rarely</th><th>Sometimes</th><th>Frequently</th></tr></thead>
          <tbody>
            {questions.map((q,i)=>(
              <tr key={q.id}>
                <td>{i+1}</td>
                <td>{q.description}</td>
                {[1,2,3].map(v=>(
                  <td key={v} className="text-center">
                    <input type="radio" name={`q-${i}`} checked={resp[i]===v}
                      onChange={()=>setResp({...resp,[i]:v})} />
                  </td>
                ))}
              </tr>
            ))}
            <tr><td colSpan={5} className="text-end"><strong>Total: {totalScore}</strong></td></tr>
          </tbody>
        </table>
      )}
    </div>
  );
}
