import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function AdminPage() {
  const API = process.env.REACT_APP_API_URL;
  const navigate = useNavigate();
  const [surveys, setSurveys] = useState([]);
  const [selectedSurvey, setSelectedSurvey] = useState('');
  const [questions, setQuestions] = useState([]);
  const [editing, setEditing] = useState({});
  const [newQ, setNewQ] = useState({ category: '', description: '', weight: 1 });
  const [newSurveyTitle, setNewSurveyTitle] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get(`${API}/usf/me`, { withCredentials: true })
      .then(r => {
        if (!r.data.logged_in) navigate('/login');
        else loadSurveys();
      }).catch(() => navigate('/login'));
  }, []);

  const loadSurveys = () => {
    axios.get(`${API}/usf/surveys`, { withCredentials: true })
      .then(r => setSurveys(r.data.surveys))
      .catch(() => setError('Error loading surveys'));
  };

  const loadQuestions = sid => {
    axios.get(`${API}/usf/surveys/${sid}`, { withCredentials: true })
      .then(r => setQuestions(r.data.questions))
      .catch(() => setError('Error loading questions'));
  };

  const handleSurveyCreate = () => {
    axios.post(`${API}/usf/surveys`, { title: newSurveyTitle }, { withCredentials: true })
      .then(() => { setNewSurveyTitle(''); loadSurveys(); })
      .catch(() => setError('Error creating survey'));
  };

  const handleSurveySelect = e => {
    const sid = e.target.value;
    setSelectedSurvey(sid);
    loadQuestions(sid);
  };

  const handleCreate = () => {
    axios.post(`${API}/usf/questions`, { ...newQ, survey_id: selectedSurvey }, { withCredentials: true })
      .then(() => {
        setNewQ({ category: '', description: '', weight: 1 });
        loadQuestions(selectedSurvey);
      }).catch(() => setError('Error creating question'));
  };

  const handleUpdate = q => {
    axios.put(`${API}/usf/questions/${q.id}`, q, { withCredentials: true })
      .then(() => loadQuestions(selectedSurvey));
  };

  const handleDelete = id => {
    axios.delete(`${API}/usf/questions/${id}`, { withCredentials: true })
      .then(() => loadQuestions(selectedSurvey));
  };

  return (
    <div className="container my-4">
      <h2>Survey Admin</h2>
      {error && <p className="text-danger">{error}</p>}

      <div className="mb-3">
        <input
          type="text"
          className="form-control mb-2"
          placeholder="New Survey Title"
          value={newSurveyTitle}
          onChange={e => setNewSurveyTitle(e.target.value)}
        />
        <button className="btn-crimson mb-3" onClick={handleSurveyCreate}>Create Survey</button>
        <select className="form-control" value={selectedSurvey} onChange={handleSurveySelect}>
          <option value="">-- Select Survey --</option>
          {surveys.map(s => <option key={s.id} value={s.id}>{s.title}</option>)}
        </select>
      </div>

      {selectedSurvey && (
        <>
          <h4>Add Question</h4>
          <div className="d-flex mb-3 gap-2">
            <input type="text" className="form-control" placeholder="Category" value={newQ.category} onChange={e => setNewQ({ ...newQ, category: e.target.value })} />
            <input type="text" className="form-control" placeholder="Description" value={newQ.description} onChange={e => setNewQ({ ...newQ, description: e.target.value })} />
            <input type="number" className="form-control" style={{ width: '80px' }} min="1" value={newQ.weight} onChange={e => setNewQ({ ...newQ, weight: e.target.value })} />
            <button className="btn-crimson" onClick={handleCreate}>Add</button>
          </div>

          <h4>Questions</h4>
          {questions.map(q => (
            <div key={q.id} className="card mb-2">
              <div className="card-body">
                <div className="d-flex justify-content-between">
                  <strong>Q#{q.id}</strong>
                  <div className="d-flex gap-2">
                    <button className="btn btn-sm btn-primary" onClick={() => handleUpdate(editing[q.id] || q)}>Save</button>
                    <button className="btn btn-sm btn-danger" onClick={() => handleDelete(q.id)}>Delete</button>
                  </div>
                </div>
                <input className="form-control my-1" value={editing[q.id]?.category ?? q.category} onChange={e => setEditing({ ...editing, [q.id]: { ...q, category: e.target.value } })} />
                <input className="form-control my-1" value={editing[q.id]?.description ?? q.description} onChange={e => setEditing({ ...editing, [q.id]: { ...q, description: e.target.value } })} />
                <input type="number" className="form-control my-1" style={{ width: '100px' }} value={editing[q.id]?.weight ?? q.weight} onChange={e => setEditing({ ...editing, [q.id]: { ...q, weight: e.target.value } })} />
              </div>
            </div>
          ))}
        </>
      )}
    </div>
  );
}
