import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function AdminPage() {
  const API = process.env.REACT_APP_API_URL;
  const navigate = useNavigate();
  const [surveys, setSurveys] = useState([]);            // list of surveys
  const [selected, setSelected] = useState(null);        // selected survey id
  const [form, setForm] = useState({ title: '', description: '' });
  const [questions, setQuestions] = useState([]);
  const [editing, setEditing] = useState({});
  const [newQ, setNewQ] = useState({ text: '', type: 'text' });
  const [error, setError] = useState('');

  const loadSurveys = () => {
    axios.get(`${API}/usf/surveys`, { withCredentials: true })
      .then(r => setSurveys(r.data.surveys))
      .catch(() => setError('Error loading surveys'));
  };

  const loadSurvey = (surveyId) => {
    setSelected(surveyId);
    axios.get(`${API}/usf/surveys/${surveyId}`, { withCredentials: true })
      .then(r => {
        setForm({ title: r.data.title, description: r.data.description });
        setQuestions(r.data.questions);
        setEditing({});
      })
      .catch(() => setError('Error loading survey'));
  };

  useEffect(() => {
    axios.get(`${API}/usf/me`, { withCredentials: true })
      .then(r => {
        if (r.data.user !== 'ADMIN') navigate('/login');
        else loadSurveys();
      })
      .catch(() => navigate('/login'));
  }, []);

  const saveSurveyMeta = () => {
    axios.put(`${API}/usf/surveys/${selected}`, form, { withCredentials: true })
      .then(loadSurveys)
      .catch(() => setError('Error saving survey'));
  };

  const handleUpdate = (q) => {
    axios.put(`${API}/usf/questions/${q.id}`, q, { withCredentials: true })
      .then(() => loadSurvey(selected))
      .catch(() => setError('Error updating question'));
  };

  const handleDelete = (id) => {
    axios.delete(`${API}/usf/questions/${id}`, { withCredentials: true })
      .then(() => loadSurvey(selected))
      .catch(() => setError('Error deleting question'));
  };

  const handleCreate = () => {
    axios.post(`${API}/usf/questions`, { ...newQ, survey_id: selected }, { withCredentials: true })
      .then(() => {
        setNewQ({ text: '', type: 'text' });
        loadSurvey(selected);
      })
      .catch(() => setError('Error creating question'));
  };

  return (
    <div className="container my-4">
      <h2>Survey Admin</h2>
      {error && <p className="text-danger">{error}</p>}

      <div className="mb-3">
        <label>Select Survey:</label>
        <select className="form-control" value={selected || ''} onChange={e => loadSurvey(e.target.value)}>
          <option value="" disabled>Pick a survey...</option>
          {surveys.map(s => (
            <option key={s.id} value={s.id}>{s.title}</option>
          ))}
        </select>
      </div>

      {selected && (
        <>
          <h4>Edit Survey Info</h4>
          <div className="d-flex gap-2 mb-3">
            <input
              className="form-control"
              value={form.title}
              placeholder="Survey Title"
              onChange={e => setForm({ ...form, title: e.target.value })}
            />
            <input
              className="form-control"
              value={form.description}
              placeholder="Survey Description"
              onChange={e => setForm({ ...form, description: e.target.value })}
            />
            <button className="btn-crimson" onClick={saveSurveyMeta}>
              Save Survey
            </button>
          </div>

          <h4>Questions</h4>
          <div className="d-flex gap-2 mb-3">
            <input
              className="form-control"
              placeholder="Question Text"
              value={newQ.text}
              onChange={e => setNewQ({ ...newQ, text: e.target.value })}
            />
            <select
              className="form-control"
              value={newQ.type}
              onChange={e => setNewQ({ ...newQ, type: e.target.value })}
            >
              <option value="text">Text</option>
              <option value="scale">Scale</option>
            </select>
            <button className="btn-crimson" onClick={handleCreate}>Add</button>
          </div>

          <table className="table">
            <thead className="table-light">
              <tr><th>ID</th><th>Text</th><th>Type</th><th>Actions</th></tr>
            </thead>
            <tbody>
              {questions.map(q => (
                <tr key={q.id}>
                  <td>{q.id}</td>
                  <td>
                    <input
                      className="form-control"
                      value={editing[q.id]?.text ?? q.text}
                      onChange={e => setEditing({
                        ...editing,
                        [q.id]: { ...q, text: e.target.value }
                      })}
                    />
                  </td>
                  <td>
                    <select
                      className="form-control"
                      value={editing[q.id]?.type ?? q.type}
                      onChange={e => setEditing({
                        ...editing,
                        [q.id]: { ...q, type: e.target.value }
                      })}
                    >
                      <option value="text">Text</option>
                      <option value="scale">Scale</option>
                    </select>
                  </td>
                  <td className="d-flex gap-2">
                    <button className="btn btn-primary btn-sm" onClick={() => handleUpdate(editing[q.id] || q)}>Save</button>
                    <button className="btn btn-danger btn-sm" onClick={() => handleDelete(q.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}
