// src/pages/SurveyPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import QuestionCard from '../components/QuestionCard';
import NavButtons from '../components/NavButtons';
import '../style.css';

export default function SurveyPage() {
  const [questions, setQuestions] = useState([]);
  const [resp, setResp] = useState({});
  const [idx, setIdx] = useState(0);
  const [desktop, setDesktop] = useState(window.innerWidth > 768);
  const [user, setUser] = useState(null);
  // state to handle mobile intro screen
  const [showIntro, setShowIntro] = useState(true);

  const API = process.env.REACT_APP_API_URL;

  useEffect(() => {
    // track window size
    const onResize = () => setDesktop(window.innerWidth > 768);
    window.addEventListener('resize', onResize);

    // load questions
    axios.get(`${API}/usf/questions`)
      .then(r => setQuestions(r.data.questions))
      .catch(console.error);

    // get current user
    axios.get(`${API}/usf/me`, { withCredentials: true })
      .then(r => {
        if (r.data.logged_in) setUser(r.data.user);
      })
      .catch(() => {});

    return () => window.removeEventListener('resize', onResize);
  }, [API]);

  const isAdmin = user === 'ADMIN';
  const totalScore = Object.entries(resp).reduce(
    (sum, [i, val]) => sum + (questions[i]?.weight || 0) * val,
    0
  );

  return (
    <div className="container my-4">
      {/* Show header and description always on desktop, or on mobile intro */}
      {(desktop || showIntro) && (
        <>
          <h2>Associate 180° Assessment</h2>
          <p><strong>Team Name:</strong></p>
          <p>
            The Associate 180° Assessment has been developed to help managers better assess—
            through observation and experience—the extent and effectiveness he or she engages in
            coaching activities and behaviors and provides a working environment conducive to
            coaching and motivating their team. Coaches and Managers may use the team cumulative
            results (no individual results will be shared, only average team scores will be shared),
            along with other learning tools to determine what areas of coaching may need more time,
            focus, and attention. It is important to be unbiased and candid in your response to
            ensure the Coach or Manager obtains the maximum benefit from this assessment for the team.
          </p>
          <p>
            <strong>Direction:</strong> The Associate 180° Assessment (1–35) consists of thought-provoking
            concepts related to effective coaching and management. Please select the number that best
            identifies the extent to which you engage in this activity or behavior. Select your answers
            according to the following three-point scale:
          </p>

          {/* Next button for mobile intro */}
          {!desktop && (
            <div className="text-center mb-4">
              <button
                className="btn-crimson"
                onClick={() => setShowIntro(false)}
                disabled={questions.length === 0}
              >
                Next
              </button>
            </div>
          )}
        </>
      )}

      {/* Mobile questions: render after clicking Next */}
      {!desktop && !showIntro && questions.length > 0 && (
        <>
          <QuestionCard
            question={questions[idx]}
            value={resp[idx] || 0}
            onChange={v => {
                // record the response
                setResp(prev => ({ ...prev, [idx]: v }));
                // auto-advance to next question or submit
                if (idx < questions.length - 1) {
                  setIdx(idx + 1);
                } else {
                  alert(`Score: ${totalScore}`);
                }
              }}
            index={idx}
            total={questions.length}
          />
          <NavButtons
            idx={idx}
            max={questions.length - 1}
            onPrev={() => {
              if (idx > 0) setIdx(idx - 1);
              else setShowIntro(true);
            }}
            onNext={() => setIdx(idx + 1)}
            onSubmit={() => alert(`Score: ${totalScore}`)}
          />
          <p className="text-center mt-3">
            Total Score: <strong>{totalScore}</strong>
          </p>
        </>
      )}

      {/* Desktop full table view */}
      {desktop && (
        <div className="desktop-table">
          <table className="table table-striped table-hover">
            <thead className="usf-table-header">
              <tr>
                {isAdmin && <th>Category</th>}
                <th>#</th>
                <th>Description</th>
                <th>Rarely</th>
                <th>Sometimes</th>
                <th>Frequently</th>
              </tr>
            </thead>
            <tbody>
              {questions.length === 0 ? (
                <tr>
                  <td colSpan={isAdmin ? 6 : 5} className="text-center py-4">
                    Loading…
                  </td>
                </tr>
              ) : (
                questions.map((q, i) => (
                  <tr key={i}>
                    {isAdmin && <td>{q.category}</td>}
                    <td>{q.id}</td>
                    <td>{q.description}</td>
                    {[1, 2, 3].map(val => (
                      <td key={val}>
                        <input
                          type="radio"
                          name={`q-${i}`}
                          checked={resp[i] === val}
                          onChange={() => setResp({ ...resp, [i]: val })}
                        />
                      </td>
                    ))}
                  </tr>
                ))
              )}
            </tbody>
          </table>
          {questions.length > 0 && (
            <p className="mt-2">
              Total Score: <strong>{totalScore}</strong>
            </p>
          )}
        </div>
      )}
    </div>
  );
}
