import ReactConfetti from 'react-confetti';
import { useWindowSize } from 'react-use';
import { useNavigate, useParams } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import QuestionCard from '../components/QuestionCard';
import NavButtons from '../components/NavButtons';
import '../style.css';

export default function SurveyPage() {
  const { surveyId } = useParams();
  const API = process.env.REACT_APP_API_URL;
  const [questions, setQuestions] = useState([]);
  const [resp, setResp] = useState({});
  const [idx, setIdx] = useState(0);
  const [submitted, setSubmitted] = useState(false);
  const { width, height } = useWindowSize();
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`${API}/usf/surveys/${surveyId}`, { withCredentials: true })
      .then(r => setQuestions(r.data.questions))
      .catch(() => navigate('/'));
  }, [API, surveyId]);

  const totalScore = Object.entries(resp).reduce((sum, [_, v]) => sum + (v || 0), 0);

  const handleSubmit = () => {
    axios.post(`${API}/usf/responses`, { surveyId, answers: resp }, { withCredentials: true })
      .then(() => setSubmitted(true))
      .catch(() => alert('Failed to submit'));
  };

  if (submitted) {
    return (
      <div className="thank-you text-center">
        <ReactConfetti width={width} height={height} />
        <h2>Thanks for your responses!</h2>
      </div>
    );
  }

  return (
    <div className="container my-4">
      {questions[idx] && (
        <>
          <QuestionCard
            question={questions[idx]}
            value={resp[questions[idx].id] || ''}
            onChange={v => setResp({ ...resp, [questions[idx].id]: v })}
            index={idx}
            total={questions.length}
          />
          <NavButtons
            idx={idx}
            max={questions.length - 1}
            onPrev={() => setIdx(idx - 1)}
            onNext={() => setIdx(idx + 1)}
            onSubmit={handleSubmit}
          />
        </>
      )}
    </div>
  );
}
