import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function AdminPage() {
  const API = process.env.REACT_APP_API_URL;
  const [surveys, setSurveys] = useState([]);
  const [selected, setSelected] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [newSurvey, setNewSurvey] = useState({ title:'', description:'' });
  const [newQ, setNewQ] = useState({ category:'', description:'', weight:1 });
  const [editing, setEditing] = useState({});
  const [error, setError] = useState('');
  const nav = useNavigate();

  useEffect(() => {
    axios.get(`${API}/usf/me`, { withCredentials:true })
      .then(r => !r.data.logged_in ? nav('/login') : loadSurveys())
      .catch(() => nav('/login'));
  }, []);

  const loadSurveys = () => {
    axios.get(`${API}/usf/surveys`,{ withCredentials:true })
      .then(r => setSurveys(r.data.surveys))
      .catch(()=>setError('Error loading surveys'));
  };

  const loadQuestions = (sid) => {
    axios.get(`${API}/usf/surveys/${sid}/questions`,{ withCredentials:true })
      .then(r => setQuestions(r.data.questions))
      .catch(()=>setError('Error loading questions'));
  };

  const selectSurvey = sid => {
    setSelected(sid);
    loadQuestions(sid);
  };

  const createSurvey = () => {
    axios.post(`${API}/usf/surveys`, newSurvey, { withCredentials:true })
      .then(()=>{ setNewSurvey({title:'',description:''}); loadSurveys(); })
      .catch(()=>setError('Could not create survey'));
  };

  const handleCreateQ = () => {
    axios.post(`${API}/usf/surveys/${selected}/questions`, newQ, { withCredentials:true })
      .then(()=>{ setNewQ({category:'',description:'',weight:1}); loadQuestions(selected); })
      .catch(()=>setError('Could not add question'));
  };

  const handleUpdateQ = q => {
    axios.put(`${API}/usf/questions/${q.id}`, q, { withCredentials:true })
      .then(()=>loadQuestions(selected));
  };
  const handleDeleteQ = id => {
    axios.delete(`${API}/usf/questions/${id}`,{ withCredentials:true })
      .then(()=>loadQuestions(selected));
  };

  return (
    <div className="container my-4">
      <h2>Survey Admin</h2>
      {error && <div className="alert alert-danger">{error}</div>}

      <div className="mb-3">
        <h5>Create New Survey</h5>
        <input placeholder="Title" className="form-control mb-1"
          value={newSurvey.title}
          onChange={e=>setNewSurvey({...newSurvey, title:e.target.value})}
        />
        <textarea placeholder="Description" className="form-control mb-1"
          value={newSurvey.description}
          onChange={e=>setNewSurvey({...newSurvey, description:e.target.value})}
        />
        <button className="btn-crimson" onClick={createSurvey}>Add Survey</button>
      </div>

      <div className="mb-3">
        <label>Select Survey:</label>
        <select className="form-select" onChange={e=>selectSurvey(e.target.value)} value={selected||""}>
          <option value="">-- pick one --</option>
          {surveys.map(s=><option key={s.id} value={s.id}>{s.title}</option>)}
        </select>
      </div>

      {selected && <>
        <h4>Editing "{surveys.find(s=>s.id==selected).title}"</h4>
        <div className="mb-3 d-flex gap-2">
          <input placeholder="Category" className="form-control"
            value={newQ.category}
            onChange={e=>setNewQ({...newQ, category:e.target.value})}
          />
          <input placeholder="Description" className="form-control"
            value={newQ.description}
            onChange={e=>setNewQ({...newQ, description:e.target.value})}
          />
          <input type="number" min="1" className="form-control" style={{width:'80px'}}
            value={newQ.weight}
            onChange={e=>setNewQ({...newQ, weight:e.target.value})}
          />
          <button className="btn-crimson" onClick={handleCreateQ}>Add Q</button>
        </div>

        <table className="table">
          <thead><tr><th>#</th><th>Category</th><th>Description</th><th>Weight</th><th>Actions</th></tr></thead>
          <tbody>
            {questions.map(q=>(
              <tr key={q.id}>
                <td>{q.id}</td>
                <td><input className="form-control"
                  value={editing[q.id]?.category||q.category}
                  onChange={e=>setEditing({...editing,[q.id]:{...q,category:e.target.value}})}
                /></td>
                <td><input className="form-control"
                  value={editing[q.id]?.description||q.description}
                  onChange={e=>setEditing({...editing,[q.id]:{...q,description:e.target.value}})}
                /></td>
                <td><input type="number" min="1" className="form-control" style={{width:'80px'}}
                  value={editing[q.id]?.weight||q.weight}
                  onChange={e=>setEditing({...editing,[q.id]:{...q,weight:e.target.value}})}
                /></td>
                <td className="d-flex gap-1">
                  <button className="btn btn-sm btn-primary"
                          onClick={()=>handleUpdateQ(editing[q.id]||q)}>Save</button>
                  <button className="btn btn-sm btn-danger"
                          onClick={()=>handleDeleteQ(q.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </>}
    </div>
  );
}
